INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (277,'Garlond GL-II','Summon forth your Garlond GL-II. For those who''ve got a nice place─who never wanted to be anybody else.','This prototype magitek vehicle was developed by Garlond Ironworks to be a mode of transportation for a new era. A compact-but-powerful twin-bank ceruleum engine provides effortless acceleration which, coupled with the rugged chassis, allows it to devour rough terrain─such as that one might see on a desert island.','Latest technology! Revolutionary ideas!    - Razel Huizan the Stationary','Terrestrial',1,237,1240,'6.2',NULL,'false','24%','https://ffxivcollect.com/images/mounts/large/277.png','https://ffxivcollect.com/images/mounts/small/277.png','https://ffxivcollect.com/music/BGM_Ride_MJI.ogg','Island Sanctuary','24,000 Seafarer''s Cowries - Rank 10',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (306,'Lynx Of Imperious Wind','Summon forth your lynx of imperious wind, which could be what a certain tempestuous archfiend might look like as a large, winged feline.','A phantom creation whose elemental aspect is tipped strongly towards astral wind. Its blustery wings call to mind the caprice of Barbariccia, archfiend of wind, but the origins of this familiar are shrouded in unrelenting storms.','Motes against the tempest. In vain do they struggle...    - Sonu Vanu','Terrestrial',1,236,2180,'6.2',NULL,'false','5.7%','https://ffxivcollect.com/images/mounts/large/306.png','https://ffxivcollect.com/images/mounts/small/306.png','https://ffxivcollect.com/music/BGM_Ride_Barb.ogg','Trial','Storm''s Crown (Extreme)','Instance',20085,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (305,'Sunforged','Summon forth your sunforged. Its searing flames consume all─with the exception of its owner, thankfully.','A product of the genius mind of Hephaistos, this fiery phantom creation combines two concepts─a flying snake and an immortal bird─and can shapeshift as the situation demands. Only the individual it recognizes as its master may ride it unscathed.','It tastes like burning...    - Hapless Boy','Airborne',1,235,97,'6.2',NULL,'false','8.8%','https://ffxivcollect.com/images/mounts/large/305.png','https://ffxivcollect.com/images/mounts/small/305.png','https://ffxivcollect.com/music/BGM_Ride_Pandemonium02.ogg','Raid','Abyssos: The Eighth Circle (Savage)','Instance',30123,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (302,'Starlight Steed','Summon forth your Starlight steed, which, despite being an illusion, can bring very real joy to one and all.','The reindeer Blitzen, visiting from Sharlayan, gifted you this ridable glamour as a show of gratitude for your kind aid. From the creature itself to the presents, all is recreated in such impressive detail that it could only be considered a Starlight miracle.','It''s a Starlight miracle!    - Nalipponcque','Terrestrial',1,232,97,'6.2',NULL,'false','35%','https://ffxivcollect.com/images/mounts/large/302.png','https://ffxivcollect.com/images/mounts/small/302.png',NULL,'Event','Starlight Celebration (2022)',NULL,NULL,'Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (299,'Wondrous Lanner','Summon forth your wondrous lanner, a proud bird with a fondness for fantastical tales.','Under the patronage of its mistress Rowena, this enchanted falcon has led a pampered life. Able to comprehend language, it loved nothing more than to listen to Khloe Aliapoh''s tales, whose heroic protagonist it aspired to bear upon its back─in contest with another of Rowena''s pets.','I doubt even my falcons could keep pace with it.    - Yugiri','Terrestrial',1,229,2100,'6.2',38457,'true','4.5%','https://ffxivcollect.com/images/mounts/large/299.png','https://ffxivcollect.com/images/mounts/small/299.png',NULL,'Wondrous Tails','1 Khloe''s Gold Certificate Of Commendation',NULL,NULL,'Wondrous Tails','600 Faux Leaves',NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (280,'Fylgja','Summon forth Fylgja, a dragon with a soft spot for heroes and songs.','Like her noble forebear Ratatoskr, Fylgja is passing fond of mankind, and traveled the world in search of mortals worthy of immortalizing in song. During the course of her journeys, she would encounter a soul who shone most brilliantly in battle. So moved was she by the hero''s selfless valor, she chose to become her follower.','Would you look at that─ my dulcet voice has drawn yet another devoted follower!    - Ulala','Terrestrial',1,217,90,'6.2',NULL,'false','10%','https://ffxivcollect.com/images/mounts/large/280.png','https://ffxivcollect.com/images/mounts/small/280.png',NULL,'PvP','Crystalline Conflict - Series 2 - Level 25',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (279,'Mechanical Lotus','Summon forth your mechanical lotus, a flower-shaped mode of transport with a spiritual aura.','Fashioned by the enigmatic Onishishu, this mechanical conveyance assumes the form of the lotus flower, a symbol of purity and sanctity in the Far East. Indeed, it gives off a distinctly spiritual aura, though how it does so is a jealously guarded secret.','Their purity is...astounding.    - Rammbroes','Airborne',1,216,2520,'6.2',NULL,'false','6.9%','https://ffxivcollect.com/images/mounts/large/279.png','https://ffxivcollect.com/images/mounts/small/279.png','https://ffxivcollect.com/music/BGM_Ride_Kyellow.ogg','Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (287,'Hippo Cart','Summon forth your hippo cart, a mean if not so lean transporting machine.','Drawing upon the design of chocobo carriages, a collective of Arkasodara youths invented this two-wheeled hippo-drawn cart. The sunshade cloth incorporates techniques used in crafting Corvosi flying carpets, and grants cart and hippo both the ability to soar the skies.','A hippo cart can outrun most anythin'' in this jungle.    - Trna','Terrestrial',1,220,92,'6.15',NULL,'false','28%','https://ffxivcollect.com/images/mounts/large/287.png','https://ffxivcollect.com/images/mounts/small/287.png',NULL,'Tribal','Ghanta - Thavnair - 18 Arkasodara Pana (Rank 7)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (294,'Megashiba','Summon forth your megashiba mount─fluffy, living proof that massive can still be adorable.','Sold as a “mameshiba,” a nonexistent miniature Far Eastern dog, this creature continued to grow until it was larger than a man. But despite its prodigious size, it has somehow retained its cuteness, which it combines with the practicality of being ridable.','He''s so fluffy!    - T''maritaa','Terrestrial',1,226,2520,'6.1',NULL,'false','14%','https://ffxivcollect.com/images/mounts/large/294.png','https://ffxivcollect.com/images/mounts/small/294.png','https://ffxivcollect.com/music/BGM_Ride_Kyellow.ogg','Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (293,'Bluefeather Lynx','Summon forth your bluefeather lynx mount, which could be what a certain feathered familiar might look like as a large, winged feline.','A phantom creation composed of the esoteric power of dynamis. It is believed that the Endsinger brought forth this mysterious entity, but for what purpose is unclear. Could it be a tribute, perhaps, bestowed upon one who has overcome her aria of despair?','The light of hope shall ever guide our steps so long as we have the will to press onward.    - Edmont de Fortemps','Terrestrial',1,225,2180,'6.1',NULL,'false','7.7%','https://ffxivcollect.com/images/mounts/large/293.png','https://ffxivcollect.com/images/mounts/small/293.png','https://ffxivcollect.com/music/BGM_Ride_GDZ.ogg','Trial','The Minstrel''s Ballad: Endsinger''s Aria','Instance',20083,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (291,'Vinegaroon','Summon forth your vinegaroon mount, a carnivorous scalekin with a jaw reminiscent of claws.','While it is common for dragons to use vinegaroons as servants, men have struggled to domesticate the creatures. In recent years, however, the gleaners have developed a successful training method, allowing them to be employed as beasts of burden.','Those vinegaroons are always chasing after us moogles, snapping their bladed jaws and swinging their bladed tails.    - Impatient Moogle','Terrestrial',1,223,94,'6.1',NULL,'false','18%','https://ffxivcollect.com/images/mounts/large/291.png','https://ffxivcollect.com/images/mounts/small/291.png',NULL,'Hunts','J''lakshai (Old Sharlayan) / Wilmetta (Radz-at-Han) - 3,200 Sacks Of Nuts',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (288,'Troll','Summon forth your troll mount, a gentle giant native to the northern seas.','Contrary to their savage appearance, trolls are highly intelligent creatures. Sharlayan scholars once sought to teach them sign language, and on account of this specimen''s friendly nature, it is believed that it was a subject in that experiment.','If I didn''t know better, I could''ve  sworn that troll was talking to you...    - Roegadyn Gleaner','Terrestrial',1,221,93,'6.1',36904,'true','6.4%','https://ffxivcollect.com/images/mounts/large/288.png','https://ffxivcollect.com/images/mounts/small/288.png',NULL,'Wondrous Tails','1 Khloe''s Gold Certificate Of Commendation',NULL,NULL,'Wondrous Tails','600 Faux Leaves',NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (284,'Pod 602','Summon forth your pod 602 mount. May leave you hanging, but will never let you go. Unless it malfunctions, or runs out of energy, or...you get the idea.','Recovered from the ruins of the machine lifeforms, this diminutive white machina was created to provide YoRHa units with movement support. On account of this, it can easily transport one person...if not exactly in style or comfort.','Those who are ready and willing to fight will never want for transportation!    - Grand Company Flyer','Airborne',1,219,2550,'6.1',NULL,'false','25%','https://ffxivcollect.com/images/mounts/large/284.png','https://ffxivcollect.com/images/mounts/small/284.png','https://ffxivcollect.com/music/BGM_Ride_YTC.ogg','Gold Saucer','300,000 MGP',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (281,'Alkonost','Summon forth your alkonost, an imposing antlered avian native to northern Ilsabard.','Captured in the Bozjan wilderness, this creature was passed around as a curiosity before eventually being released again. From the start, it seemed unperturbed in the presence of men, leading some to believe it was trained by an imperial beastmaster.','How goes the training for your avian mount?    - Luquelot','Terrestrial',1,218,91,'6.1',36906,'true','12%','https://ffxivcollect.com/images/mounts/large/281.png','https://ffxivcollect.com/images/mounts/small/281.png',NULL,'Treasure Hunt','Nesvaaz - Radz-at-Han - 3 Resplendent Feathers (The Excitatron 6000 or The Shifting Gymnasion Agonon)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (265,'Demi-Phoinix','Summon forth Demi-Phoinix. Because four wings are better than two and three heads better than one.','This entity is a recreation of the being found imprisoned inside Pandæmonium. The original was the fruit of the ancients'' research into immortal birds, but owing to its volatility, it was branded a failure.','How do you spell that again?    - An Ancient','Airborne',1,209,87,'6.05',NULL,'false','12%','https://ffxivcollect.com/images/mounts/large/265.png','https://ffxivcollect.com/images/mounts/small/265.png','https://ffxivcollect.com/music/BGM_Ride_Pandemonium01.ogg','Raid','Asphodelos: The Fourth Circle (Savage)','Instance',30108,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (274,'Porxie King','Summon forth your porxie king. Smells like bacon that''s a cut above the rest─though that might just be an effect of the crown.','Unlike its kin, this porxie was born without the ability to cure the tempered. Rather than have it go to waste, however, it was made into a mount and given a crown─perhaps to evoke a sense of luxury and comfort.','This specimen can be considered royalty among them.    - Martineau','Airborne',1,215,68,'6.0',NULL,'false','0.1%','https://ffxivcollect.com/images/mounts/large/274.png','https://ffxivcollect.com/images/mounts/small/274.png',NULL,'Premium','Lawson Promotion (Japan Only)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (273,'Wivre','Summon forth your wivre, a mighty horned beast said to be nature''s battering ram.','Native to the New World, these ill-mannered scalekin are notoriously difficult to tame. Yet where others have failed, the Mamool Ja have succeeded, their people employing wivres as beasts of burden and weapons of war both.','I pray that you at least consider a watchful trot into danger rather than a headlong charge...    - Iliud','Terrestrial',1,214,85,'6.0',36005,'true','1.3%','https://ffxivcollect.com/images/mounts/large/273.png','https://ffxivcollect.com/images/mounts/small/273.png',NULL,'FATE','Edelina - Mor Dhona - 500 Bicolor Gemstone Vouchers',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (269,'Papa Paissa','Summon forth Papa Paissa. It''s for a good cause.','This exceptionally large paissa has sired no less than eight offspring, and in order to earn the coin to rear them, it chose to work as an adventurer''s mount. By taking it into your service, you are supporting its adorable brood.','I can only imagine the burden they bear with so many mouths to feed.    - Pip','Terrestrial',1,213,88,'6.0',NULL,'false','4.8%','https://ffxivcollect.com/images/mounts/large/269.png','https://ffxivcollect.com/images/mounts/small/269.png','https://ffxivcollect.com/music/BGM_Ride_Kyellow.ogg','Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (268,'Level Checker','Summon forth your level checker, a mysterious machine that can determine your strength...and apparently also shrink and carry you inside it.','When this mysterious machine first determined your strength, the result was “level 90.” By what method it came up with its figure, and whether it is even accurate, none can possibly say.','You''ve really taken it to another level with this!    - Kai-Shirr','Terrestrial',1,212,1190,'6.0',NULL,'false','26%','https://ffxivcollect.com/images/mounts/large/268.png','https://ffxivcollect.com/images/mounts/small/268.png',NULL,'FATE','Nesvaaz - Radz-at-Han - 12 Chi Bolts (FATE "Omicron Recall: Killing Order" - Ultima Thule)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (267,'Victor','Summon forth Victor, a dog to rule over all dogs─at dog shows, that is.','Lauded by no less a personage than Emperor Varis, Victor is a perennial champion at Garlean dog shows. Being bred and trained for competition, it isn''t suited to combat, though it may at least intimidate foes with its fearsome mien.','Hmph, all bark an'' no bite!    - Rabid Wives Houndkeep','Terrestrial',1,211,86,'6.0',NULL,'false','0.7%','https://ffxivcollect.com/images/mounts/large/267.png','https://ffxivcollect.com/images/mounts/small/267.png',NULL,'Achievement','Endgame Hunter','Achievement',3000,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
