INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (245,'Landerwaffe','Summon forth Landerwaffe, magnificent mechanical dragon of legend, and be the envy of all the other kids.','Armed literally to the wings, this mechanical dragon is believed to have been created by the Allagan Empire to combat the creatures whose form it mimics. Magnificent and proud, it allows none upon its back save one whom it deems a true hero.','Wow, you''re swifter than a salamander!    - Pixie Flowerkeep','Terrestrial',2,200,2150,'5.5',NULL,'false','12%','https://ffxivcollect.com/images/mounts/large/245.png','https://ffxivcollect.com/images/mounts/small/245.png','https://ffxivcollect.com/music/BGM_Ride_EX3Comp.ogg','Quest','The Dragon Made','Quest',69593,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
