INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (228,'Jibanyan Couch','Summon forth the Jibanyan couch. A mysterious piece of furniture designed in the likeness of a Yo-kai named Jibanyan.','Despite being modeled upon Jibanyan, an Earthbound Yo-kai, this couch is able to fly. It is said to be so comfortable as to turn men and Yo-kai alike into useless layabouts.','Sluggards! Layabouts! Good-for-nothing reprobates!    - Seigetsu the Enlightened','Airborne',1,180,2530,'5.3',NULL,'false','10%','https://ffxivcollect.com/images/mounts/large/228.png','https://ffxivcollect.com/images/mounts/small/228.png',NULL,'Event','Yo-kai Watch Collaboration - Acquire 17 Yo-kai Weapons',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
