INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (223,'Rolling Tankard','Summon forth your Rolling Tankard, a mighty warmachina built by the blacksmiths of Watts''s Anvil.','Based on a concept by the legendary Watts, this dwarven warmachina is propelled by a nine-cylinder radial ethanol engine. Its turret-mounted crystal cannon makes short work of sin eaters, while its arms can perform essential motions such as upending tankards.','The Rollin'' Tankards, at your service!    - Ronitt','Terrestrial',1,176,1110,'5.3',NULL,'false','28%','https://ffxivcollect.com/images/mounts/large/223.png','https://ffxivcollect.com/images/mounts/small/223.png',NULL,'Tribal','Mizutt - Lakeland - 18 Hammered Frogments (Rank 7)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (222,'Chocobo Carriage','Summon forth your chocobo carriage to ferry your friends around in comfort.','A ubiquitous mode of transportation in Eorzea, the chocobo carriage is kept aloft by ceruleum gas-filled bladders. This technology traces its origins to an early Ixali dirigible that crashed in the Twelveswood and was seized by Gridanian authorities.','We get to ride a horsebird carriage? I don''t know... It sounds scary.    - Doman Child','Terrestrial',4,175,1,'5.3',NULL,'false','11%','https://ffxivcollect.com/images/mounts/large/222.png','https://ffxivcollect.com/images/mounts/small/222.png',NULL,'Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (216,'Pteranodon','Summon forth your pteranodon, a winged scalekin that has existed since ancient times.','In the course of feeding upon food scraps discarded at the *Bounty*, this creature grew so familiar with men as to allow riders upon its back. This goes to show that the way to a prehistoric creature''s heart is through its stomach.','With what powerful magicks did they tame this dread fiend?    - Nashu Mhakaracca','Airborne',1,169,75,'5.21',NULL,'false','0.9%','https://ffxivcollect.com/images/mounts/large/216.png','https://ffxivcollect.com/images/mounts/small/216.png','https://ffxivcollect.com/music/BGM_Ride_Hukko01.ogg','Achievement','Castle In The Sky','Achievement',2526,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (209,'Albino Karakul','Summon forth your albino karakul, a black sheep among its kind. Which is to say it''s white.','Though karakuls ordinarily have black fleece, this creature raised in the paddocks of House Haillenarte was born snowy white. This rarest of qualities spared it a fate at the dining table, and it grew to such a size as to be able to bear people upon its fluffy back.','A wise knight keeps her own source of wool.    - Yaelle','Terrestrial',1,164,73,'5.21',28125,'true','25%','https://ffxivcollect.com/images/mounts/large/209.png','https://ffxivcollect.com/images/mounts/small/209.png',NULL,'Skybuilders','8,400 Skybuilders'' Scrips',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
