INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (220,'Rubellite Carbuncle','Summon forth your Rubellite Carbuncle, an arcane entity given form by the mysteries of rubellite geometry.','Those adventurers who have longed to hop on their Carbuncles'' backs can give thanks to the arcanist Ulan, whose research into Diamond Carbuncle provided the necessary cues to create an arcane entity of ridable proportions. As its form is maintained by a large rubellite, it can be summoned even by those unversed in the ways of arcanima.','Betrayed by my own Carbuncle yet again...    - Tataru','Terrestrial',1,173,57,'5.2',NULL,'false','5.1%','https://ffxivcollect.com/images/mounts/large/220.png','https://ffxivcollect.com/images/mounts/small/220.png','https://ffxivcollect.com/music/BGM_Ride_Kyellow.ogg','Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (219,'Ramuh','Summon forth Ramuh. No, not that one. The one you fought in the Empty. But not the actual one.','It is believed that Shiva''s summoning in the Empty caused lightning-aspected aether left over from your battle with Ramuh to coalesce. The resultant crystal, when imbued with magic, gives rise to an entity which assumes the form of the Lord of Levin in all of his horse-legged, hulking glory.','Now nature sends thunder and lightning against us!    - Sylphie','Airborne',1,172,77,'5.2',NULL,'false','14%','https://ffxivcollect.com/images/mounts/large/219.png','https://ffxivcollect.com/images/mounts/small/219.png','https://ffxivcollect.com/music/BGM_Ride_Eden02.ogg','Raid','Eden''s Verse: Refulgence (Savage)','Instance',30095,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (218,'Hybodus','Summon forth your hybodus to ride the winds and menace the skies.','A close relative of the tornado shark, this fearsome fish soars the skies by means of an organ that allows it to manipulate wind-aspected aether. The four fins protruding from its head are used to deftly control its course in flight.','What will the morrow bring? Sharks spilling from the sky in a spectacular shower of scales?    - Sisipu','Airborne',1,171,76,'5.2',NULL,'false','23%','https://ffxivcollect.com/images/mounts/large/218.png','https://ffxivcollect.com/images/mounts/small/218.png',NULL,'Achievement','No More Fish In The Sea II','Achievement',2561,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (217,'Ruby Gwiber','Summon forth your ruby gwiber, a draconic arcane entity.','Believed to have been absorbed into the Ruby Weapon, this plate-clad being answers the call of a horn imbued with the power of an unknown primal. How the secrets of the horn came to be passed on to its summoner remains a mystery.','A jeweler''s daughter must be refined─*resplendent*. A ruby amongst the riffraff.    - Babawai','Terrestrial',1,170,2140,'5.2',NULL,'false','18%','https://ffxivcollect.com/images/mounts/large/217.png','https://ffxivcollect.com/images/mounts/small/217.png','https://ffxivcollect.com/music/BGM_Ride_Ruby.ogg','Trial','Cinder Drift (Extreme)','Instance',20069,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
