INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (161,'Rathalos','Summon forth your mighty Rathalos─a wyvern far, far from home.','Many a brave hunter has attempted to topple this flame-belching beast from his fiery throne atop the forest, yet only a few can claim to have conquered the King of the Skies.','The wyverns are scary. Is there no safe place?    - Wandering Gnath','Terrestrial',1,120,2550,'4.36',NULL,'false','19%','https://ffxivcollect.com/images/mounts/large/161.png','https://ffxivcollect.com/images/mounts/small/161.png','https://ffxivcollect.com/music/BGM_Ride_NIKU.ogg','Trial','The Great Hunt (Extreme)','Instance',20054,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
