INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (159,'Dodo','Summon forth your noble dodo─hopefully not the last one in existence.','For centuries people of the Far East believed the dodo lost─the cloudkin hunted to extinction by poachers who would use its feathers for petticoats and its eggs for cheesy omelettes. The recent discovery of a nest in the upper echelons of Heaven–on–High, however, has given naturalists hope that the species may still endure.','Those are some interestin''-lookin'' dodos ye''ve got there.    - Jacke','Terrestrial',1,118,59,'4.35',23023,'true','8.9%','https://ffxivcollect.com/images/mounts/large/159.png','https://ffxivcollect.com/images/mounts/small/159.png',NULL,'Deep Dungeon','Heaven-on-High - Platinum Sack',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
