INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (136,'Striped Ray','Summon forth your striped ray, a majestic creature native to the Ruby Sea.','Raised and trained by the Kojin, this striped ray has the same mellow temperament as its original masters. Its flat body works in like fashion to the aether-imbued wings of an airship, allowing it to catch water- and wind-aspected aether to glide through both sea and sky with ease.','I advise you to stand back when he regurgitates everything.    - Zukin','Airborne',1,100,56,'4.1',NULL,'false','25%','https://ffxivcollect.com/images/mounts/large/136.png','https://ffxivcollect.com/images/mounts/small/136.png',NULL,'Tribal','Shikitahe - Tamamizu - 12 Kojin Sango (Rank 8)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (133,'Legendary Kamuy','Summon forth the legendary kamuy, blessed wolf who shall not be bound by such corporeal laws as gravity or aerodynamics.','Perpetually enveloped in a divine glow, it is said to have been born when an Ala Mhigan war hound chanced to bathe in Shinryu''s light in the moment of the primal''s birth.','It is a delicate dance... and one which asks much of my hounds.    - Zenos yae Galvus','Terrestrial',1,99,2120,'4.1',NULL,'false','42%','https://ffxivcollect.com/images/mounts/large/133.png','https://ffxivcollect.com/images/mounts/small/133.png','https://ffxivcollect.com/music/BGM_Ride_Shinryu.ogg','Trial','The Minstrel''s Ballad: Shinryu''s Domain','Instance',20050,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (99,'Starlight Bear','Summon forth your Starlight bear, and delight the younglings to no end.','Unlike his wild cousins, Kochab the bear can be relied upon to delight folk rather than tear them apart. Raised from a cub by Celebrant Baenfaeld, he has mastered the art of Starburst-lobbing, a skill that proved useful in hunting down thieving treants.','I never knew bears could be so adorable!    - Festival-goer','Terrestrial',1,74,55,'4.1',NULL,'false','13%','https://ffxivcollect.com/images/mounts/large/99.png','https://ffxivcollect.com/images/mounts/small/99.png',NULL,'Event','Starlight Celebration (2017)',NULL,NULL,'Premium','Online Store',NULL,NULL,NULL,NULL,NULL,NULL);
