INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (93,'Managarm','Summon forth Managarm, a legendary hound said to dwell in the hidden depths of Abalathia''s Spine.','Some say that Managarm is merely a myth. Others believe it to be an extinct beast. Both are wrong, as you well know. According to Abalathian legend, this fearsome hound roams the celestial sphere in pursuit of the moon, whose light it relishes─or so it may appear to those who witness it soaring the skies, spawning spectral flames.','I should have been the one to feed your dark hound with light!    - From an Ancient Epic','Terrestrial',1,68,33,'4.1',NULL,'false','8.0%','https://ffxivcollect.com/images/mounts/large/93.png','https://ffxivcollect.com/images/mounts/small/93.png',NULL,'Premium','Calamity Salvager - 8 Gold Chocobo Feathers (Recruit a Friend Campaign)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (112,'Falcon','Call into battle the *Falcon*, (unofficially) the fastest single-rider airship ever built in Eorzea.','An independent project of Garlond Ironworks engineer Wedge, this single-rider airship allegedly recorded some of the fastest flight speeds ever...until a tragic accident following an attempt to see how high the ship could soar convinced its creator that perhaps a modicum of restraint was necessary.','All you have to do is follow the falcon. He knows where to go.    - Baurendouin de Haillenarte','Terrestrial',1,96,1020,'4.05',NULL,'false','36%','https://ffxivcollect.com/images/mounts/large/112.png','https://ffxivcollect.com/images/mounts/small/112.png',NULL,'Limited','Fly the Falcon Mount Campaign',NULL,NULL,'Event','Moogle Treasure Trove Event',NULL,NULL,NULL,NULL,NULL,NULL);
