INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (21,'Gilded Magitek Armor','Call into battle your gilded suit of magitek armor. Its golden coating serves to resist magicks while impressing members of the opposite sex.','It only took a few heated skirmishes with the allied city–states for Garlemald to realize that the magicks wielded by the realm''s mages were too much for even the thickest of magitek armor plating. Alloyed gilding has since been discovered to dampen the effects of elemental charges and is now standard issue.','You ditched your magitek armor!? Fool of a Lalafell!   - Biggs','Terrestrial',1,10,4,'2.1',NULL,'false','41%','https://ffxivcollect.com/images/mounts/large/21.png','https://ffxivcollect.com/images/mounts/small/21.png','https://ffxivcollect.com/music/BGM_Ride_MagitekArmor.ogg','Achievement','Everybody''s Darling','Achievement',862,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (20,'Laurel Goobbue','Summon your laurel goobbue, towering friend to the sylphs.','After having the moss and flowers growing upon his head pulled up and tossed aside like common weeds by a band of sylvan ne''er-do-wells, this quiet-mannered goobbue was taken in and cared for by the sylphs of Little Solace whose elders crafted for him a magnificent laurel with which to hide his scars.','Naught is as exhilarating as a romp through the forest on goobbueback.   - Unknown','Terrestrial',1,9,6,'2.1',NULL,'false','34%','https://ffxivcollect.com/images/mounts/large/20.png','https://ffxivcollect.com/images/mounts/small/20.png',NULL,'Tribal','Sylphic Vendor - Little Solace - 120,000 Gil (Rank 4)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (19,'Cavalry Drake','Summon your cavalry drake, trained by the drake whisperer of the Brotherhood of Ash.','Training a cavalry drake is a long and arduous process that begins the moment the scalekin hatches. Never allowed to see its mother, the creature is raised solely in the presence of a drake whisperer while being weaned on the incendiary glands of aged battle drakes to ensure it is both submissive and deadly.','You are as a lost kitten that has wandered into the drake''s lair.   - Zagozz Teh','Terrestrial',1,8,10,'2.1',NULL,'false','30%','https://ffxivcollect.com/images/mounts/large/19.png','https://ffxivcollect.com/images/mounts/small/19.png',NULL,'Tribal','Amalj''aa Vendor - Ring of Ash - 120,000 Gil (Rank 4)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (18,'Behemoth','Summon your mighty behemoth, trained from birth to carry you...not trample you underfoot like an ant.','Immediately following the Calamity, an adventurer returning to Gridania from Falcon''s Nest in Coerthas claims to have happened upon a den of baby behemoths who had lost their mother. Despite objections from friends and family alike, she kept the beasts, eventually training them to serve as mounts.','Do not presume to provoke the behemoth to battle. You will die.   - Meurise','Terrestrial',1,7,9,'2.1',NULL,'false','68%','https://ffxivcollect.com/images/mounts/large/18.png','https://ffxivcollect.com/images/mounts/small/18.png',NULL,'Achievement','Jonathas - Old Gridania - 6 Achievement Certificates',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (15,'Unicorn','Summon forth the single-horned steed you befriended in the Black Shroud.','Not long ago, Eorzean unicorns numbered in the thousands, and could oft be seen roaming the Coerthas highlands in great majestic herds that would render the mountains white as snow. Poaching has caused the population to decline, and if it were not for your act of kindness, there would be one fewer.','Your precious horses, they''re just unicorns with a big empty forehead.   - Chief Chocowacker','Terrestrial',1,6,2000,'2.0',NULL,'false','47%','https://ffxivcollect.com/images/mounts/large/15.png','https://ffxivcollect.com/images/mounts/small/15.png',NULL,'Quest','Unicorn Power','Quest',65730,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (9,'Ahriman','Summon forth a riding ahriman, bound to this realm with powerful magicks guaranteed to never fade.','Though little is known of the void and the creatures which call the extra-dimensional realm home, scholars have discovered that there is a strict hierarchy amongst voidsent consisting of twelve distinct tiers. Ahrimans are believed to fall into the fifth, making them formidable foes...when not enthralled by curses.','Ye''d ''ave t'' be blinder''n an ahriman wi'' an eyepatch t'' miss the camp.   - Baderon Tenfingers','Airborne',1,5,8,'2.0',NULL,'false','60%','https://ffxivcollect.com/images/mounts/large/9.png','https://ffxivcollect.com/images/mounts/small/9.png',NULL,'Achievement','Jonathas - Old Gridania - 6 Achievement Certificates',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO mountproducts(`id`,`name`,`description`,`enhanced_description`,`tooltip`,`movement`,`seats`,`order`,`order_group`,`patch`,`item_id`,`tradeable`,`owned`,`image`,`icon`,`bgm`,`sources0type`,`sources0text`,`sources0related_type`,`sources0related_id`,`sources1type`,`sources1text`,`sources1related_type`,`sources1related_id`,`sources2type`,`sources2text`,`sources2related_type`,`sources2related_id`) VALUES (8,'Coeurl','Summon your fleet-footed battle coeurl, trained from a pup to recognize you as a friend...not food.','Native to the jungles of the Near East, coeurls were only recently introduced to Eorzea, brought over from faraway lands such as Thavnair by Ul''dahn nobles who thought to breed them as pets. Now, but a handful of skilled tamers living in recluse on the Pearl are said to be able to train them.','Hey, good lookin''! Catch my coeurl yet?   - Swygrael','Terrestrial',1,4,7,'2.0',NULL,'false','36%','https://ffxivcollect.com/images/mounts/large/8.png','https://ffxivcollect.com/images/mounts/small/8.png',NULL,'Premium','A Realm Reborn Collector''s Edition',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
